<?php$route = '/ongithub/businesses/';$app->get($route, function ()  use ($app){	if(isset($_REQUEST['query'])){ $query = $_REQUEST['query']; } else { $query = '';}	$ObjectText = file_get_contents('https://raw.github.com/ongithub/businesses/gh-pages/businesses.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 1;		$id = $Object['id'];		$name = $Object['name'];		$description = $Object['description'];		$url = $Object['url'];		$keywords = $Object['keywords'];		$streetAddress = $Object['streetAddress'];		$addressLocality = $Object['addressLocality'];		$addressRegion = $Object['addressRegion'];		$postalCode = $Object['postalCode'];		$telephone = $Object['telephone'];		$email = $Object['email'];		$map = $Object['map'];		$latitude = $Object['latitude'];		$longitude = $Object['longitude'];		if($query!=''){		$IncludeRecord = 0;			if(strpos(strtolower($name),strtolower($query)) === 0 || strpos(strtolower($description),strtolower($query)) === 0 || strpos(strtolower($keywords),strtolower($query)) === 0 || strpos(strtolower($streetAddress),strtolower($query)) === 0 || strpos(strtolower($addressRegion),strtolower($query)) === 0)				{				$IncludeRecord = 1;				}			}		if($IncludeRecord==1)			{			$F = array();			$F['id'] = $id;			$F['name'] = $name;			$F['description'] = $description;			$F['url'] = $url;			$F['keywords'] = $keywords;			$F['streetAddress'] = $streetAddress;			$F['addressLocality'] = $addressLocality;			$F['addressRegion'] = $addressRegion;			$F['postalCode'] = $postalCode;			$F['telephone'] = $telephone;			$F['email'] = $email;			$F['map'] = $map;			$F['latitude'] = $latitude;			$F['longitude'] = $longitude;			array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});$route = '/ongithub/businesses/:id';$app->get($route, function ($Inco